/**
 * Replicates [serde_json::Value], but with `uniffi` support.
 */
sealed class Json {
    /**
     * JSON `null`
     */
    object Null : Json()

    /**
     * JSON `true` or `false`
     */
    data class Bool(
        /**
         * The boolean
         */
        val `bool`: kotlin.Boolean,
    ) : Json() {
        companion object
    }

    /**
     * JSON integer or float
     */
    data class Numb(
        /**
         * The integer or float
         */
        val `number`: Number,
    ) : Json() {
        companion object
    }

    /**
     * JSON string
     */
    data class Str(
        /**
         * The string
         */
        val `string`: kotlin.String,
    ) : Json() {
        companion object
    }

    /**
     * JSON array of JSON
     */
    data class Array(
        /**
         * The array
         */
        val `array`: List<Json>,
    ) : Json() {
        companion object
    }

    /**
     * JSON object
     */
    data class Object(
        /**
         * The object
         */
        val `object`: Map<kotlin.String, Json>,
    ) : Json() {
        companion object
    }

    companion object
}

